mapper.select:
SELECT A.mw_num, A.n_num, A.m_num, A.u_num, ifnull(E.u_nick, E.u_mail) as nick, A.w_why, date_format(A.mw_date, '%y. %m. %d. (%H:%i)')as mw_date, F.b_stop, C.n_title, B.m_title, D.U_NUM as wU_num, D.U_NICK as wNick, D.U_LEVEL FROM m_warning A left join (select m_num, m_title from memo) B on A.m_num = B.m_num left join (select n_title, n_num, u_num as writter from novel)C on A.n_num = c.n_num LEFT JOIN (SELECT U_NUM, U_NICK, U_LEVEL from user) D ON c.writter = D.u_num LEFT JOIN (SELECT U_NUM, U_NICK, u_mail from user) E ON A.u_num = E.u_num LEFT JOIN (SELECT m_num, count(mw_num) as b_stop from m_warning group by m_num) F ON A.m_num = F.m_num
WHERE BY nick = #{keyword}
WHERE BY n_title = #{keyword}
WHERE BY wNick = #{keyword}
WHERE BY n_num = #{keyword}
ORDER BY mw_date DESC Limit #{start},10

SELECT count(A.mw_num) as cnt FROM m_warning A left join (select m_num, b_stop from memo) B on A.m_num = B.m_num left join (select n_title, n_num, u_num as writter from novel)C on A.n_num = c.n_num LEFT JOIN (SELECT U_NUM, U_NICK, U_LEVEL from user) D ON c.writter = D.u_num LEFT JOIN (SELECT U_NUM, U_NICK, u_mail from user) E ON A.u_num = E.u_num LEFT JOIN (SELECT m_num, count(mw_num) as b_stop from m_warning group by m_num) F ON A.m_num = F.m_num;
WHERE BY nick = #{keyword}
WHERE BY n_title = #{keyword}
WHERE BY wNick = #{keyword}
